import React from 'react';
import './App.css';
import TodoList from './components/Todo/TodoList';

function App() {
  const todos = [
    { id: 1, completed: false, title: 'Buy some shit' },
    { id: 3, completed: false, title: 'Buy some shit' },
    { id: 2, completed: false, title: 'Buy some shit' },
  ];

  function onToggleTodo(id) {
    todos = todos.map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
    });
  }

  return (
    <div className='wrapper'>
      <h1>Todo list</h1>
      <TodoList todos={todos} onToggle={onToggleTodo} />
    </div>
  );
}

export default App;
