import React from 'react';

function TodoItem({ todo, index, onClick }) {
  //there we take some fields from props which is default
  return (
    <li className='todo-item'>
      <span>
        <input className='todo-item__check' type='checkbox' onClick={()=>{console.log(todo.id)}}/>
        <strong>{index + 1})</strong>
        &nbsp;
        {todo.title}
      </span>
      <button className='todo-item__btn-remove'>&times;</button>
    </li>
  );
}

export default TodoItem;
